import React, { useState, useEffect, useRef } from 'react';

const TargetPracticeGame = () => {
  const canvasRef = useRef(null);
  const [score, setScore] = useState(0);
  const [target, setTarget] = useState({ x: 0, y: 0, radius: 20 });

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    const drawTarget = () => {
      const { x, y, radius } = target;
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.beginPath();
      ctx.arc(x, y, radius, 0, Math.PI * 2);
      ctx.fillStyle = 'red';
      ctx.fill();
      ctx.strokeStyle = 'black';
      ctx.lineWidth = 2;
      ctx.stroke();
      ctx.closePath();
    };

    const handleMouseClick = (event) => {
      const mouseX = event.clientX - canvas.getBoundingClientRect().left;
      const mouseY = event.clientY - canvas.getBoundingClientRect().top;

      // Check if the click is inside the target
      if (
        mouseX >= target.x - target.radius &&
        mouseX <= target.x + target.radius &&
        mouseY >= target.y - target.radius &&
        mouseY <= target.y + target.radius
      ) {
        // Increase the score and update the game state
        setScore((prevScore) => prevScore + 1);
        setTarget({
          x: Math.random() * canvas.width,
          y: Math.random() * canvas.height,
          radius: 20,
        });
      }
    };

    canvas.addEventListener('click', handleMouseClick);

    return () => {
      canvas.removeEventListener('click', handleMouseClick);
    };
  }, [target, score]);

  return (
    <div>
      <p>Score: {score}</p>
      <canvas ref={canvasRef} width={800} height={600}></canvas>
    </div>
  );
};

export default TargetPracticeGame;
